#include <stdio.h>

int binary_search(int A[], int N, int B) {
    int left = 0;
    int right = N - 1;

    while (left <= right) {
        int mid = left + (right - left) / 2;
        if (A[mid] == B) {
            return mid;
        } else if (A[mid] < B) {
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }

    return -1;
}

int main() {
    int N, B;
    scanf("%d", &N);
    int A[N];

    for (int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
    }
    scanf("%d", &B);

    int result = binary_search(A, N, B);
    printf("%d\n", result);

    return 0;
}
